/* CSS declarations go here */
// @import './defalut.scss';
/* font */

html {
  line-height: 1.15;
  -webkit-text-size-adjust: 100%;
}

body {
  height: 100vh;
  width: 100vw;
  margin: 0px;
  padding: 0px;
}

input {
  outline: 0;
}

.safe-top {
  padding-top: env(safe-area-inset-top);
}

.safe-bottom {
  padding-bottom: env(safe-area-inset-bottom);
}

* {
  outline-style: none;
  padding: 0px;
  margin: 0px;
  font-size: 0.24rem;
}

.icon {
  background-repeat: no-repeat;
  background-size: contain;
}

.bg {
  background-repeat: no-repeat;
  background-size: cover;
}

*,
.font-hei {
  font-family: -apple-system, 'Helvetica Neue', Helvetica, 'Nimbus Sans L',
    Arial, 'Liberation Sans', 'PingFang SC', 'Hiragino Sans GB',
    'Source Han Sans CN', 'Source Han Sans SC', 'Microsoft YaHei',
    'Wenquanyi Micro Hei', 'WenQuanYi Zen Hei', 'ST Heiti', SimHei,
    'WenQuanYi Zen Hei Sharp', sans-serif;
}

.font-kai {
  font-family: Baskerville, Georgia, 'Liberation Serif', 'Kaiti SC', STKaiti,
    'AR PL UKai CN', 'AR PL UKai HK', 'AR PL UKai TW', 'AR PL UKai TW MBE',
    'AR PL KaitiM GB', KaiTi, KaiTi_GB2312, DFKai-SB, 'TW\-Kai', serif;
}

.font-song {
  font-family: Georgia, 'Nimbus Roman No9 L', 'Songti SC', STSong,
    'AR PL New Sung', 'AR PL SungtiL GB', NSimSun, SimSun, 'TW\-Sung',
    'WenQuanYi Bitmap Song', 'AR PL UMing CN', 'AR PL UMing HK',
    'AR PL UMing TW', 'AR PL UMing TW MBE', PMingLiU, MingLiU, serif;
}

.font-fang-song {
  font-family: Baskerville, 'Times New Roman', 'Liberation Serif', STFangsong,
    FangSong, FangSong_GB2312, 'CWTEX\-F', serif;
}

hr {
  box-sizing: content-box;
  height: 0;
  overflow: visible;
}

a {
  background-color: transparent;
}

abbr[title] {
  border-bottom: none;
  text-decoration: underline;
  text-decoration: underline dotted;
}

b,
strong {
  font-weight: bolder;
}

code,
kbd,
samp {
  font-family: monospace, monospace; // font-size: 1em
}

small {
  font-size: 80%;
}

img {
  border-style: none;
}

button,
input,
optgroup,
select,
textarea {
  line-height: 1.15;
  margin: 0;
}

button,
input {
  overflow: visible;
}

button,
select {
  text-transform: none;
}

button,
[type='button'],
[type='reset'],
[type='submit'] {
  -webkit-appearance: button;
}

button::-moz-focus-inner,
[type='button']::-moz-focus-inner,
[type='reset']::-moz-focus-inner,
[type='submit']::-moz-focus-inner {
  border-style: none;
  padding: 0;
}

button:-moz-focusring,
[type='button']:-moz-focusring,
[type='reset']:-moz-focusring,
[type='submit']:-moz-focusring {
  outline: 1px dotted ButtonText;
}

legend {
  box-sizing: border-box;
  color: inherit;
  display: table;
  max-width: 100%;
  padding: 0;
  white-space: normal;
}

progress {
  vertical-align: baseline;
}

textarea {
  overflow: auto;
}

[type='checkbox'],
[type='radio'] {
  box-sizing: border-box;
  padding: 0;
}

[type='number']::-webkit-inner-spin-button,
[type='number']::-webkit-outer-spin-button {
  height: auto;
}

[type='search'] {
  -webkit-appearance: textfield;
  outline-offset: -2px;
}

[type='search']::-webkit-search-decoration {
  -webkit-appearance: none;
}

::-webkit-file-upload-button {
  -webkit-appearance: button;
  font: inherit;
}

details {
  display: block;
}

summary {
  display: list-item;
}

template {
  display: none;
}

[hidden] {
  display: none;
}

@media screen and (min-width: 320px) {
  html {
    font-size: 50px;
  }
}

@media screen and (min-width: 360px) {
  html {
    font-size: 56.25px;
  }
}

@media screen and (min-width: 375px) {
  html {
    font-size: 58.59375px;
  }
}

@media screen and (min-width: 400px) {
  html {
    font-size: 62.5px;
  }
}

@media screen and (min-width: 414px) {
  html {
    font-size: 64.6875px;
  }
}

@media screen and (min-width: 440px) {
  html {
    font-size: 68.75px;
  }
}

@media screen and (min-width: 480px) {
  html {
    font-size: 75px;
  }
}

@media screen and (min-width: 640px) {
  html {
    font-size: 66.67px;
  }
}

@media (max-width: 639px) {
  * {
    -webkit-tap-highlight-color: transparent;
    -webkit-touch-callout: none;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  }
}

body {
  overflow-x: hidden;
}

@media screen and (min-width: 460px) {
  ::-webkit-scrollbar {
    width: 7px;
    height: 7px;
    background-color: #f5f5f5;
  }
  ::-webkit-scrollbar-track {
    border-radius: 3.5px;
    background-color: #f5f5f5;
  }
  ::-webkit-scrollbar-thumb {
    border-radius: 3.5px;
    background-color: #c0c0c0;
    opacity: 0.7;
    transition: opacity ease-in-out 200ms;
  }
  ::-webkit-scrollbar-thumb:hover {
    opacity: 1;
  }
}
