import 'package:flutter/material.dart';

class Iconfont extends StatelessWidget {
  final int codePoint;
  final String fontFamily;
  final Color color;
  final int hex;
  final String name;
  final double size;

  static data(int codePoint, { String fontFamily = 'iconfont' }) {
    return IconData(codePoint, fontFamily: fontFamily);
  }

  Iconfont(this.codePoint,
      {this.name='', this.color = const Color(0xFFFF0000), this.fontFamily='iconfont', this.size, this.hex=0xFFFF0000})

  @override
  Widget build(BuildContext context) {
    return new Icon(Iconfont.data(codePoint, fontFamily: fontFamily), color: hex != null ? new Color(hex) :color, size: size,);
  }
}
