import React, { PureComponent } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { bindActionCreators } from 'redux';
import withStyles from '@blankapp/ui/src/withStyles';
import { TextInput } from '../../components';
import { setNote } from '../../redux/actions/shoppingCart';
import Lang from '../../utilities/Lang';

class OrderConfirmSetNote extends PureComponent {
  handleNoteChange = (event) => {
    this.note = event.nativeEvent.text;
  };
  render() {
    /* eslint no-shadow:0 */
    const {setNote, ...rest} = this.props
    return (
      <TextInput
        autoCapitalize="none"
        placeholder={Lang.screens.orderConfirm.writeDownYourNote}
        autoCorrect={false}
        onChange={this.handleNoteChange}
        onBlur={this.handleSetNote}
        {...rest}
      />
    );
  }
}

OrderConfirmSetNote.propTypes = {
  setNote: PropTypes.func,
};

/* eslint no-unused-vars:0 */
function mapStateToProps(state, ownProps) {
  return {};
}

function mapDispatchToProps(dispatch) {
  return {
    setNote: bindActionCreators(setNote, dispatch),
  };
}

export default withStyles('OrderConfirmSetNote')(connect(mapStateToProps, mapDispatchToProps)(OrderConfirmSetNote));
