using System.Collections;
using System.Collections.Generic;
using UnityEngine;

public class FlexCanvas : MonoBehaviour
{
    public bool isUpdate = false;
    public bool horizontal = false;
    public float designWidth = 640f;
    public float designHeight = 1136f;
    public float scale = 1;

    Canvas canvas;
    void Start()
    {
        canvas = this.GetComponent<Canvas>();
        runFixScaleFactor();
    }
    void runFixScaleFactor()
    {
        if (horizontal)
        {
            float dp = designHeight / designWidth;
            float sp = (float)Screen.height / (float)Screen.width;
            fixScaleFactor(designHeight, Screen.height, dp, sp);
        }
        else
        {
            float dp = designWidth / designHeight;
            float sp = (float)Screen.width / (float)Screen.height;
            fixScaleFactor(designWidth, Screen.width, dp, sp);
        }
    }
    void fixScaleFactor(float design, float screen, float dp, float sp)
    {
        // 画布缩放比例
        if (design < screen)
        {
            scale = dp * design / screen;
        }
        else
        {
            scale = dp * screen / design;
        }
        canvas.scaleFactor = scale;
    }

    // test
    void Update()
    {
        if (isUpdate)
        {
            runFixScaleFactor();
        }
    }
}
